<?include_once 'app/moduli/menu_amm/operazioni/oggetti/OperazioneDefault.php';class sovvenzioni extends OperazioneDefault {		public function __construct() { }		public function preInsert($arrayParametri = array()) {		global $enteAdmin;			$this->caricaValoriProceduraRelativa();	}		public function preUpdate($arrayParametri = array()) {		global $enteAdmin;			$this->caricaValoriProceduraRelativa();	}			public function postInsert($arrayParametri = array()) {		if(moduloAttivo('ealbo')) {			$this->inserisciRecordAtto();		}		$this->correggiTipologia();	}		public function postDelete($arrayParametri = array()) {		if(moduloAttivo('ealbo')) {			$this->eliminaRecordAtto();		}	}		private function inserisciRecordAtto() {		global $database, $dati_db, $enteAdmin;				$_POST['id_atto_albo'] = forzaNumero($_POST['id_atto_albo']);				$sql = "SELECT * FROM ".$dati_db['prefisso']."oggetto_sovvenzioni WHERE id_ente = ".$enteAdmin['id']." ORDER BY id DESC LIMIT 1";		if(!($result = $database->connessioneConReturn($sql))) {}		$record = $database->sqlArray($result);				$atto = caricaDocumentoEAlbo('atti', $_POST['id_atto_albo']);				$sql = "INSERT INTO etrasp_atti_ealbo (id_ente, id_ente_albo, id_atto_albo, id_oggetto, id_documento, ultima_modifica) 				VALUES (".$enteAdmin['id'].", ".$enteAdmin['id_ente_albo'].", ".$_POST['id_atto_albo'].", 38, ".$record['id'].", ".$atto['ultima_modifica'].")";		if(!($result = $database->connessioneConReturn($sql))) {}				collegaAttoEtrasparenza($_POST['id_atto_albo'], 1, $enteAdmin);	}		private function eliminaRecordAtto() {		global $database, $dati_db, $enteAdmin;				$cancello = isset($_POST['id_cancello_tabella']) ? forzaStringa($_POST['id_cancello_tabella']) : 0;		$arrayOggetti = explode(",", $cancello);		$arrayOggettiAlbo = array();		$numeroOggetti = count($arrayOggetti)-1;		$condizione = 'id_documento='.$arrayOggetti[0];		$arrayOggettiAlbo[] = getIdRecordAtto($enteAdmin['id'], $enteAdmin['id_ente_albo'], $arrayOggetti[0], 38);		for ($i=1;$i<$numeroOggetti+1;$i++) {			if ($arrayOggetti[$i] != '') {				$condizione .= ' OR id_documento='.$arrayOggetti[$i];				$arrayOggettiAlbo[] = getIdRecordAtto($enteAdmin['id'], $enteAdmin['id_ente_albo'], $arrayOggetti[$i], 38);			}		}		$sql = "DELETE FROM ".$dati_db['prefisso']."etrasp_atti_ealbo WHERE id_ente = ".$enteAdmin['id']." AND id_ente_albo = ".$enteAdmin['id_ente_albo']." AND id_oggetto = 38 AND (".$condizione.")";		if(!($result = $database->connessioneConReturn($sql))) {}				foreach((array) $arrayOggettiAlbo as $id) {			collegaAttoEtrasparenza($id, 0, $enteAdmin);		}	}		private function correggiTipologia() {		global $database, $dati_db, $enteAdmin;			$sql = "SELECT * FROM ".$dati_db['prefisso']."oggetto_sovvenzioni WHERE id_ente = ".$enteAdmin['id']." ORDER BY id DESC LIMIT 1";		if(!($result = $database->connessioneConReturn($sql))) {}		$record = $database->sqlArray($result);			if($record['tipologia'] == '') {			//forzo sovvenzione			$sql = "UPDATE ".$dati_db['prefisso']."oggetto_sovvenzioni SET tipologia = 'sovvenzione' WHERE id = ".$record['id'];			if(!($result = $database->connessioneConReturn($sql))) {}		}		}		private function caricaValoriProceduraRelativa() {		global $database, $dati_db, $enteAdmin;				if($_POST['id_sovvenzione']) {			//inserimento/modifica liquidazione			$id_record = forzaNumero($_POST['id_sovvenzione']);			$record = mostraDatoOggetto($id_record, 38, '*');			if($record['id']){				//$_POST['oggetto'] = addslashes($record['oggetto']);				$_POST['nominativo'] = html_entity_decode(addslashes($record['nominativo']),ENT_COMPAT | ENT_HTML401, 'ISO-8859-1');				$_POST['dati_fiscali'] = html_entity_decode(addslashes($record['dati_fiscali']),ENT_COMPAT | ENT_HTML401, 'ISO-8859-1');				$_POST['oggetto'] = html_entity_decode(addslashes($record['oggetto']),ENT_COMPAT | ENT_HTML401, 'ISO-8859-1');				$_POST['struttura'] = addslashes($record['struttura']);				$_POST['responsabile'] = addslashes($record['responsabile']);				$_POST['omissis'] = ($record['omissis']);			}		} else {			$oggOgg = new oggettiAdmin(38);			//inserimento/modifica sovvenzione: modifico le eventuali liquidazioni			$sql = "SELECT * FROM ".$dati_db['prefisso']."oggetto_sovvenzioni WHERE id_ente=".$enteAdmin['id']." "					." AND id_sovvenzione = ".forzaNumero($_GET['id'])." AND tipologia='liquidazione' "					." ORDER BY id";			if ( !($result = $database->connessioneConReturn($sql)) ) {}			$recs = $database->sqlArrayAss($result);			foreach((array)$recs as $r) {				$arrayValori = array(						'id_proprietario' => $r['id_proprietario'],						'id_lingua' => 0,						'nominativo' => forzaStringa($_POST['nominativo']),				        'dati_fiscali' => forzaStringa($_POST['dati_fiscali']),				        'oggetto' => forzaStringa($_POST['oggetto']),				        'struttura' => forzaStringa($_POST['struttura']),				        'responsabile' => forzaStringa($_POST['responsabile']),		                'omissis' => forzaStringa($_POST['omissis'])				);				if($oggOgg->modificaOggetto($r['id'], 0, $arrayValori)) {}			}		}	}}?>